mapper.update:
UPDATE BO 
	SET BO_TITLE =#{bo_title}, BO_CONTENT =#{bo_content} ,MOD_DATE =SYSDATE, MOD_ID=#{mod_id}
	WHERE BO_NO = #{bo_no}

UPDATE BO SET BO_DEL_YN ='Y'
	WHERE BO_NO IN 

	#{bo_no}

mapper.select:
SELECT COUNT(*)
	FROM VIEW_COUNT
	WHERE BO_NO =#{bo_no} AND EMP_NO = #{emp_no}

mapper.delete:
DELETE FROM LIKER 
	WHERE BO_NO =#{bo_no}
	AND EMP_NO=#{emp_no}

mapper.select:
SELECT COUNT(*)
	FROM LIKER
	WHERE BO_NO =#{bo_no} AND EMP_NO = #{emp_no}

SELECT BO_NO, EMP_NO, LIKE_YN
    FROM LIKER
    WHERE BO_NO=#{bo_no} AND EMP_NO=#{emp_no}
